#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;
class Solution
{
public:
    int solve(string A);

    };


bool checkPlaindrome(string A, int left, int right){
    //cout<<left<<":"<<right<<endl;
    if(left>=right) return true;

    if(A[left]!=A[right]){
        return false;
    }
    return checkPlaindrome(A,left+1,right-1);

}
int Solution::solve(string A) {
    return checkPlaindrome(A,0,A.length()-1);

}



int main()
{
    Solution s;
    string A = "";
    cout <<s.solve(A);
 
    return 0;
}